<?php #narrative_view.inc$narrYear = $narrative->narrYear;$stuGrade = get_current_grade( $narrative->baseGrade, $narrative->baseYear, $narrYear);$report_grade = $narrative->narrGrade;if($letter_grade){	$report_grade = $letter_grade;}?><h3><?="$studentName: $narrative->narrSubject for Grade $stuGrade, $narrative->narrTerm $narrative->narrYear";?></h3><p><a  href="<?=site_url("narrative/student_list/$narrative->kStudent");?>" class='button'>Back to Narratives</a></p><p><span class='highlight'>Last Modified: <?=format_timestamp( $narrative->recModified); ?> by <?="$recModifier->teachFirst $recModifier->teachLast"; ?></span></p><p>Written by: <?=$teacher;?></p> <? if($hasSuggestions): ?> <p><span class="highlight">Has Suggested Edits</span></p><? endif; ?><!-- middle schoolers get letter grades in some classes --><? if($stuGrade >= 5): ?><p>Report Grade: <?=$report_grade; ?></p><? endif; ?><div class='button-bar'><span class='button edit edit_narrative' id='n_<?=$narrative->kNarrative;?>'>Edit Original</span><?if($benchmarks_available): ?><span class='button edit_student_benchmarks' id='b_<?=$narrative->kNarrative;?>'>Edit Benchmarks</span><? endif;?><?if($backups):?><a href="<?=site_url("narrative/list_backups/$narrative->kNarrative");?>"  class='button backup_list' id='backup_<?=$narrative->kNarrative;?>'>Show Backups</a><?endif;?></div><input type='hidden' id='kStudent' name='kStudent' value='<?=$narrative->kStudent?>'/><div class="narrText"><?=stripslashes($narrative->narrText);?></div><? if(!empty($benchmarks)){	$this->load->view("benchmark/chart", array("benchmarks" => $benchmarks,"legend"=>$legend));}?>