<?php defined('BASEPATH') OR exit('No direct script access allowed');//define variables outside the html for less clutter.$currentYear = get_current_year();$stuDOB = format_date($student->stuDOB, 'standard');//create a human-friendly version of the enrollment status.$enrollmentStatus = "Enrolled";if($student->isEnrolled == 0){	$enrollmentStatus = "Not Enrolled";}$graduateStatus = FALSE;if($student->isGraduate == 1 && $student->stuGrade > 8){	$graduateStatus = "Yes";}$teacherLabel = "Classroom Teacher";if($student->stuGrade > 4){	$teacherLabel = "Middle School Advisor";}?>		<?php $this->load->view("student/navigation", array("kStudent" => $student->kStudent));?>		<div class='content inner'>			<?php			if( $this->session->userdata("dbRole") != 3 ):			$edit_buttons[] = array("href"=>site_url("student/edit/$student->kStudent"), "class"=>"student_edit dialog button edit", "id"=>"es_$student->kStudent", "text"=>"Edit Info");			print create_button_bar($edit_buttons);		endif;		?>		<div class="column-group">			<div class="block column-main" id="student-info">			<h3>General Info</h3>			<p>				<input type="hidden" name="target" value="student">			</p>			<p>				<label>First Name: </label>				<?php  echo $student->stuFirst;?>			</p>			<p>				<label>Last Name: </label>				<?php  echo $student->stuLast;?>			</p>			<p>				<label>Nickname: </label>				<?php  echo $student->stuNickname;?>			</p>			<p>				<label>Birthdate: </label>				<?php  echo $stuDOB;?>			</p>			<p>				<label>Grade at Enrollment:</label>				<?php  echo format_grade($student->baseGrade);?>			</p>			<p>				<label>School Year of Enrollment: </label>				<?php  echo $student->baseYear;?>				-				<?php  echo $student->baseYear+1;?>			</p>				<p>				<label>Enrollment Status: </label>				<?php  echo $enrollmentStatus;?>			</p>			<p>				<label>Current Grade: </label>				<?php  echo format_grade($student->stuGrade);?>			</p>			<?php if($student->stuGrade > 4): ?>                <p>                    <label>Group: </label>                    <?php if($student->stuGrade < 7):                        print "5/6 $student->stuGroup";                    else:                        print "7/8 $student->stuGroup";                    endif; ?>                </p>            <?php endif; //current grade > 4?>			<p>				<label>Perferred Pronouns: </label>				<?php  echo $student->pronouns;?>			</p>			<?php if($student->isEnrolled == 1 ): ?>			<p>				<label><?php  echo $teacherLabel; ?>: </label>				<?php  echo format_name($student->teachFirst,$student->teachLast);?>			</p>			<?php if($student->stuGrade > 4):?>                <p>                <label>Humanities Teacher: </label>                			<?php  echo format_name($student->humanitiesFirst,$student->humanitiesLast);?>                </p>              <?endif; //end if grade > 4 ?>             <?php endif; ?>		</div>		<div class="column-secondary">		<div class="block">			<h3>Email</h3>			<p>				<label>Address: </label>				<?php  echo format_email(get_value($student,"stuEmail"),"TRUE");?>			</p>			<p>				<label>Parent Permission to Use Email: </label>				<?php if(get_value($student,'stuEmailPermission') == 1):					echo "Yes";				else:					echo "No";				endif; ?>			</p>			<div class='password-box'>				<label class='link' title='click to toggle'>Show Password</label>&nbsp;				<span class='password-field'> <?php  echo get_value($student,'stuEmailPassword');?>				</span>			</div>		</div>		<?php if($graduateStatus == "Yes") :?>			<p>			<label>Is a Graduate:</label>			<?php  echo $graduateStatus;?>			</p>		<?php endif; //if enrolled ?>		<!--  grade preferences are only used for middleschoolers and enrolled students.  -->		<?php if(get_value($student,"isEnrolled") == 1 && get_current_grade($student->baseGrade, $student->baseYear) > 4):?>			<div class="block" id="grade-preferences">				<h3>Grade Preferences</h3>				<p>Grade Preferences are for students who may be uniquely graded as					pass/fail in subjects traditionally graded with letter grades</p>				<?php if(!empty($grade_preferences)):?>				<?php $this->load->view("grade_preference/list",array('grade_preferences' =>$grade_preferences));?>				<?php endif;?>				<?php  echo create_button_bar(array(array(						"href"=>site_url("grade_preference/create/$student->kStudent"),						"text"=>"Add Grade Preference",						"class" => "link new dialog",						"selection" => "grade_preference",						"id" => sprintf("add-grade-preference_%s", $student->kStudent),					)));?>			</div>		<?php endif;?>		</div>	</div></div>